export default function AdPackagesScreen() {
  const router = useRouter();
  const [selectedPackage, setSelectedPackage] = useState<number | null>(null);

  const handlePackageSelect = (packageId: number) => {
    setSelectedPackage(packageId);
  };

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.title}>Рекламные пакеты</Text>
        <Text style={styles.subtitle}>Готовые решения для вашей рекламной кампании</Text>
      </View>

      <ScrollView style={styles.content}>
        {AD_PACKAGES.map((pack, index) => (
          <Animated.View
            key={pack.id}
            entering={FadeInRight.delay(index * 100)}
            style={styles.packageCard}
          >
            <Image source={{ uri: pack.image }} style={styles.packageImage} />
            <View style={styles.packageContent}>
              <Text style={styles.packageTitle}>{pack.title}</Text>
              <Text style={styles.packageDescription}>{pack.description}</Text>
              
              <View style={styles.packageDetails}>
                <View style={styles.detailItem}>
                  <Calendar size={16} color={COLORS.textLight} />
                  <Text style={styles.detailText}>{pack.duration}</Text>
                </View>
                <View style={styles.detailItem}>
                  <MapPin size={16} color={COLORS.textLight} />
                  <Text style={styles.detailText}>{pack.coverage}</Text>
                </View>
                <View style={styles.detailItem}>
                  <DollarSign size={16} color={COLORS.textLight} />
                  <Text style={styles.detailText}>{pack.price.toLocaleString()} ₽</Text>
                </View>
              </View>

              <Text style={styles.spotsTitle}>Рекламные места в пакете:</Text>
              {pack.spots.map(spot => (
                <View key={spot.id} style={styles.spotItem}>
                  <View style={styles.spotIcon}>
                    <Check size={16} color={COLORS.primary} />
                  </View>
                  <View style={styles.spotInfo}>
                    <Text style={styles.spotTitle}>{spot.title}</Text>
                    <Text style={styles.spotLocation}>{spot.location}</Text>
                  </View>
                </View>
              ))}

              <TouchableOpacity 
                style={[
                  styles.selectButton,
                  selectedPackage === pack.id && styles.selectButtonActive
                ]}
                onPress={() => handlePackageSelect(pack.id)}
              >
                <Text style={[
                  styles.selectButtonText,
                  selectedPackage === pack.id && styles.selectButtonTextActive
                ]}>
                  {selectedPackage === pack.id ? 'Выбрано' : 'Выбрать пакет'}
                </Text>
              </TouchableOpacity>
            </View>
          </Animated.View>
        ))}
      </ScrollView>

      {selectedPackage && (
        <View style={styles.bottomBar}>
          <TouchableOpacity 
            style={styles.orderButton}
            onPress={() => router.push('/order-package')}
          >
            <Text style={styles.orderButtonText}>Оформить заказ</Text>
          </TouchableOpacity>
        </View>
      )}
    </View>
  );
}

